arrayPipelines.data:
[
  {
    $search: {
      index: "title_index",
      text: {
        query: {{Book_title_search.text}},
        path: {
          wildcard: "*"
        }
      }
    }
  },
  {
	$match:{
			"platform": "{{String(PlatformSelect.selectedOptionValue)}}",
		}
	},
	{
  "$limit": 25
  },
	{
    $sort: {
			"timeSeries.views":-1
  	}
	},
  {
  $project: {
			_id:1,
			bookId:1,
      title: 1,
			authorName:1,
			inCatalogue:1,
      genre: 1,
			platform:1,
      tags: 1,
      chaptersTotal: 1,
      bookCreateTime: 1,
      rating: 1,
      gender: 1,
			status:1,
      numOfRatings: 1,
			bookUrl:1,
			lastUpdateTime:1,
		  pfmStatus:1,
		  pfmShowTitle:1,
		  pfmShowId:1,
      views: {
        $arrayElemAt: ["$timeSeries.views", 0],
      },
    }
  }
]

